import { Pool, neonConfig } from '@neondatabase/serverless';
import { drizzle } from 'drizzle-orm/neon-serverless';
import ws from "ws";
import * as schema from "@shared/schema";

neonConfig.webSocketConstructor = ws;

if (!process.env.DATABASE_URL) {
  throw new Error(
    "DATABASE_URL must be set. Did you forget to provision a database?",
  );
}

export const pool = new Pool({ connectionString: process.env.DATABASE_URL });
export const db = drizzle({ client: pool, schema });

server/index.ts.content:
import express, { type Request, Response, NextFunction } from "express";
import { registerRoutes } from "./routes";
import { setupVite, serveStatic, log } from "./vite";

const app = express();
app.use(express.json());
app.use(express.urlencoded({ extended: false }));

app.use((req, res, next) => {
  const start = Date.now();
  const path = req.path;
  let capturedJsonResponse: Record<string, any> | undefined = undefined;

  const originalResJson = res.json;
  res.json = function (bodyJson, ...args) {
    capturedJsonResponse = bodyJson;
    return originalResJson.apply(res, [bodyJson, ...args]);
  };

  res.on("finish", () => {
    const duration = Date.now() - start;
    if (path.startsWith("/api")) {
      let logLine = `${req.method} ${path} ${res.statusCode} in ${duration}ms`;
      if (capturedJsonResponse) {
        logLine += ` :: ${JSON.stringify(capturedJsonResponse)}`;
      }

      if (logLine.length > 80) {
        logLine = logLine.slice(0, 79) + "â€¦";
      }

      log(logLine);
    }
  });

  next();
});

(async () => {
  const server = await registerRoutes(app);

  app.use((err: any, _req: Request, res: Response, _next: NextFunction) => {
    const status = err.status || err.statusCode || 500;
    const message = err.message || "Internal Server Error";

    res.status(status).json({ message });
    throw err;
  });

  // importantly only setup vite in development and after
  // setting up all the other routes so the catch-all route
  // doesn't interfere with the other routes
  if (app.get("env") === "development") {
    await setupVite(app, server);
  } else {
    serveStatic(app);
  }

  // ALWAYS serve the app on the port specified in the environment variable PORT
  // Other ports are firewalled. Default to 5000 if not specified.
  // this serves both the API and the client.
  // It is the only port that is not firewalled.
  const port = parseInt(process.env.PORT || '5000', 10);
  server.listen({
    port,
    host: "0.0.0.0",
    reusePort: true,
  }, () => {
    log(`serving on port ${port}`);
  });
})();

server/replitAuth.ts.content:
import * as client from "openid-client";
// @ts-ignore - Module resolution issue with openid-client/passport
import { Strategy, type VerifyFunction } from "openid-client/passport";

import passport from "passport";
import session from "express-session";
import type { Express, RequestHandler } from "express";
import memoize from "memoizee";
import connectPg from "connect-pg-simple";
import { storage } from "./storage";

if (!process.env.REPLIT_DOMAINS) {
  throw new Error("Environment variable REPLIT_DOMAINS not provided");
}

const getOidcConfig = memoize(
  async () => {
    return await client.discovery(
      new URL(process.env.ISSUER_URL ?? "https://replit.com/oidc"),
      process.env.REPL_ID!
    );
  },
  { maxAge: 3600 * 1000 }
);

export function getSession() {
  const sessionTtl = 7 * 24 * 60 * 60 * 1000; // 1 week
  const pgStore = connectPg(session);
  const sessionStore = new pgStore({
    conString: process.env.DATABASE_URL,
    createTableIfMissing: false,
    ttl: sessionTtl,
    tableName: "sessions",
  });
  return session({
    secret: process.env.SESSION_SECRET!,
    store: sessionStore,
    resave: false,
    saveUninitialized: false,
    cookie: {
      httpOnly: true,
      secure: true,
      maxAge: sessionTtl,
    },
  });
}

function updateUserSession(
  user: any,
  tokens: client.TokenEndpointResponse & client.TokenEndpointResponseHelpers
) {
  user.claims = tokens.claims();
  user.access_token = tokens.access_token;
  user.refresh_token = tokens.refresh_token;
  user.expires_at = user.claims?.exp;
}

async function upsertUser(
  claims: any,
) {
  await storage.upsertUser({
    id: claims["sub"],
    email: claims["email"],
    firstName: claims["first_name"],
    lastName: claims["last_name"],
    profileImageUrl: claims["profile_image_url"],
  });
}

export async function setupAuth(app: Express) {
  app.set("trust proxy", 1);
  app.use(getSession());
  app.use(passport.initialize());
  app.use(passport.session());

  const config = await getOidcConfig();

  const verify: VerifyFunction = async (
    tokens: client.TokenEndpointResponse & client.TokenEndpointResponseHelpers,
    verified: passport.AuthenticateCallback
  ) => {
    const user = {};
    updateUserSession(user, tokens);
    await upsertUser(tokens.claims());
    verified(null, user);
  };

  for (const domain of process.env
    .REPLIT_DOMAINS!.split(",")) {
    const strategy = new Strategy(
      {
        name: `replitauth:${domain}`,
        config,
        scope: "openid email profile offline_access",
        callbackURL: `https://${domain}/api/callback`,
      },
      verify,
    );
    passport.use(strategy);
  }

  passport.serializeUser((user: Express.User, cb) => cb(null, user));
  passport.deserializeUser((user: Express.User, cb) => cb(null, user));

  app.get("/api/login", (req, res, next) => {
    passport.authenticate(`replitauth:${req.hostname}`, {
      prompt: "login consent",
      scope: ["openid", "email", "profile", "offline_access"],
    })(req, res, next);
  });

  app.get("/api/callback", (req, res, next) => {
    passport.authenticate(`replitauth:${req.hostname}`, {
      successReturnToOrRedirect: "/",
      failureRedirect: "/api/login",
    })(req, res, next);
  });

  app.get("/api/logout", (req, res) => {
    req.logout(() => {
      res.redirect(
        client.buildEndSessionUrl(config, {
          client_id: process.env.REPL_ID!,
          post_logout_redirect_uri: `${req.protocol}://${req.hostname}`,
        }).href
      );
    });
  });
}

export const isAuthenticated: RequestHandler = async (req, res, next) => {
  const user = req.user as any;

  if (!req.isAuthenticated() || !user.expires_at) {
    return res.status(401).json({ message: "Unauthorized" });
  }

  const now = Math.floor(Date.now() / 1000);
  if (now <= user.expires_at) {
    return next();
  }

  const refreshToken = user.refresh_token;
  if (!refreshToken) {
    res.status(401).json({ message: "Unauthorized" });
    return;
  }

  try {
    const config = await getOidcConfig();
    const tokenResponse = await client.refreshTokenGrant(config, refreshToken);
    updateUserSession(user, tokenResponse);
    return next();
  } catch (error) {
    res.status(401).json({ message: "Unauthorized" });
    return;
  }
};

server/routes.ts.content:
import type { Express } from "express";
import { createServer, type Server } from "http";
import { storage } from "./storage";
import { setupAuth, isAuthenticated } from "./replitAuth";
import { WebSocketManager } from "./websocket";
import { 
  insertRideSchema, 
  insertVehicleSchema, 
  insertRideRequestSchema,
  insertReviewSchema 
} from "@shared/schema";
import { z } from "zod";

// PayPal imports (comment out to prevent errors when no credentials)
// import { createPaypalOrder, capturePaypalOrder, loadPaypalDefault } from "./paypal";

export async function registerRoutes(app: Express): Promise<Server> {
  // Auth middleware
  await setupAuth(app);

  // Auth routes
  app.get('/api/auth/user', isAuthenticated, async (req: any, res) => {
    try {
      const userId = (req.user as any).claims.sub;
      const user = await storage.getUser(userId);
      res.json(user);
    } catch (error) {
      console.error("Error fetching user:", error);
      res.status(500).json({ message: "Failed to fetch user" });
    }
  });

  // User profile routes
  app.put('/api/profile', isAuthenticated, async (req: any, res) => {
    try {
      const userId = (req.user as any).claims.sub;
      const updateData = req.body;

      const user = await storage.updateUserProfile(userId, updateData);
      res.json(user);
    } catch (error) {
      console.error("Error updating profile:", error);
      res.status(500).json({ message: "Failed to update profile" });
    }
  });

  // Vehicle routes
  app.post('/api/vehicles', isAuthenticated, async (req: any, res) => {
    try {
      const userId = (req.user as any).claims.sub;
      const vehicleData = insertVehicleSchema.parse({
        ...req.body,
        userId
      });

      const vehicle = await storage.createVehicle(vehicleData);
      res.json(vehicle);
    } catch (error) {
      console.error("Error creating vehicle:", error);
      if (error instanceof z.ZodError) {
        res.status(400).json({ message: "Invalid vehicle data", errors: error.errors });
      } else {
        res.status(500).json({ message: "Failed to create vehicle" });
      }
    }
  });

  app.get('/api/vehicles', isAuthenticated, async (req: any, res) => {
    try {
      const userId = (req.user as any).claims.sub;
      const vehicles = await storage.getUserVehicles(userId);
      res.json(vehicles);
    } catch (error) {
      console.error("Error fetching vehicles:", error);
      res.status(500).json({ message: "Failed to fetch vehicles" });
    }
  });

  app.put('/api/vehicles/:id', isAuthenticated, async (req: any, res) => {
    try {
      const vehicleId = req.params.id;
      const updateData = req.body;

      const vehicle = await storage.updateVehicle(vehicleId, updateData);
      res.json(vehicle);
    } catch (error) {
      console.error("Error updating vehicle:", error);
      res.status(500).json({ message: "Failed to update vehicle" });
    }
  });

  app.delete('/api/vehicles/:id', isAuthenticated, async (req: any, res) => {
    try {
      const vehicleId = req.params.id;
      await storage.deleteVehicle(vehicleId);
      res.json({ message: "Vehicle deleted successfully" });
    } catch (error) {
      console.error("Error deleting vehicle:", error);
      res.status(500).json({ message: "Failed to delete vehicle" });
    }
  });

  // Ride routes
  app.post('/api/rides', isAuthenticated, async (req: any, res) => {
    try {
      const userId = (req.user as any).claims.sub;
      const rideData = insertRideSchema.parse({
        ...req.body,
        driverId: userId
      });

      const ride = await storage.createRide(rideData);
      res.json(ride);
    } catch (error) {
      console.error("Error creating ride:", error);
      if (error instanceof z.ZodError) {
        res.status(400).json({ message: "Invalid ride data", errors: error.errors });
      } else {
        res.status(500).json({ message: "Failed to create ride" });
      }
    }
  });

  app.get('/api/rides/search', async (req, res) => {
    try {
      const { origin, destination, date, minSeats } = req.query;

      const filters: any = {};
      if (origin) filters.origin = origin as string;
      if (destination) filters.destination = destination as string;
      if (date) filters.date = new Date(date as string);
      if (minSeats) filters.minSeats = parseInt(minSeats as string);

      const rides = await storage.searchRides(filters);
      res.json(rides);
    } catch (error) {
      console.error("Error searching rides:", error);
      res.status(500).json({ message: "Failed to search rides" });
    }
  });

  app.get('/api/rides/:id', async (req, res) => {
    try {
      const rideId = req.params.id;
      const ride = await storage.getRide(rideId);

      if (!ride) {
        return res.status(404).json({ message: "Ride not found" });
      }

      res.json(ride);
    } catch (error) {
      console.error("Error fetching ride:", error);
      res.status(500).json({ message: "Failed to fetch ride" });
    }
  });

  app.get('/api/rides', isAuthenticated, async (req: any, res) => {
    try {
      const userId = (req.user as any).claims.sub;
      const { type = 'driver' } = req.query;

      const rides = await storage.getUserRides(userId, type as 'driver' | 'passenger');
      res.json(rides);
    } catch (error) {
      console.error("Error fetching user rides:", error);
      res.status(500).json({ message: "Failed to fetch rides" });
    }
  });

  app.put('/api/rides/:id', isAuthenticated, async (req: any, res) => {
    try {
      const rideId = req.params.id;
      const updateData = req.body;

      const ride = await storage.updateRide(rideId, updateData);
      res.json(ride);
    } catch (error) {
      console.error("Error updating ride:", error);
      res.status(500).json({ message: "Failed to update ride" });
    }
  });

  app.delete('/api/rides/:id', isAuthenticated, async (req: any, res) => {
    try {
      const rideId = req.params.id;
      await storage.deleteRide(rideId);
      res.json({ message: "Ride deleted successfully" });
    } catch (error) {
      console.error("Error deleting ride:", error);
      res.status(500).json({ message: "Failed to delete ride" });
    }
  });

  // Ride request routes
  app.post('/api/ride-requests', isAuthenticated, async (req: any, res) => {
    try {
      const userId = (req.user as any).claims.sub;
      const requestData = insertRideRequestSchema.parse({
        ...req.body,
        passengerId: userId
      });

      const request = await storage.createRideRequest(requestData);
      res.json(request);
    } catch (error) {
      console.error("Error creating ride request:", error);
      if (error instanceof z.ZodError) {
        res.status(400).json({ message: "Invalid request data", errors: error.errors });
      } else {
        res.status(500).json({ message: "Failed to create ride request" });
      }
    }
  });

  app.get('/api/ride-requests', isAuthenticated, async (req: any, res) => {
    try {
      const userId = (req.user as any).claims.sub;
      const { rideId } = req.query;

      let requests;
      if (rideId) {
        requests = await storage.getRideRequests(rideId as string);
      } else {
        requests = await storage.getUserRideRequests(userId);
      }

      res.json(requests);
    } catch (error) {
      console.error("Error fetching ride requests:", error);
      res.status(500).json({ message: "Failed to fetch ride requests" });
    }
  });

  app.put('/api/ride-requests/:id/status', isAuthenticated, async (req: any, res) => {
    try {
      const requestId = req.params.id;
      const { status } = req.body;

      const request = await storage.updateRideRequestStatus(requestId, status);
      res.json(request);
    } catch (error) {
      console.error("Error updating request status:", error);
      res.status(500).json({ message: "Failed to update request status" });
    }
  });

  // Review routes
  app.post('/api/reviews', isAuthenticated, async (req: any, res) => {
    try {
      const userId = (req.user as any).claims.sub;
      const reviewData = insertReviewSchema.parse({
        ...req.body,
        reviewerId: userId
      });

      const review = await storage.createReview(reviewData);
      res.json(review);
    } catch (error) {
      console.error("Error creating review:", error);
      if (error instanceof z.ZodError) {
        res.status(400).json({ message: "Invalid review data", errors: error.errors });
      } else {
        res.status(500).json({ message: "Failed to create review" });
      }
    }
  });

  app.get('/api/reviews/:userId', async (req, res) => {
    try {
      const userId = req.params.userId;
      const reviews = await storage.getUserReviews(userId);
      res.json(reviews);
    } catch (error) {
      console.error("Error fetching reviews:", error);
      res.status(500).json({ message: "Failed to fetch reviews" });
    }
  });

  // OTP Verification Routes
  app.post("/api/auth/send-otp", isAuthenticated, async (req: any, res) => {
    try {
      const { phone } = req.body;
      const userId = (req.user as any).claims.sub;

      // Generate 6-digit OTP
      const otpCode = Math.floor(100000 + Math.random() * 900000).toString();
      const otpExpiry = new Date(Date.now() + 10 * 60 * 1000); // 10 minutes

      // Update user with OTP
      await storage.updateUserOTP(userId, otpCode, otpExpiry);

      // In production, send SMS via Twilio/similar service
      console.log(`OTP for ${phone}: ${otpCode}`);

      res.json({ message: "OTP sent successfully" });
    } catch (error) {
      console.error("Error sending OTP:", error);
      res.status(500).json({ message: "Failed to send OTP" });
    }
  });

  app.post("/api/auth/verify-otp", isAuthenticated, async (req: any, res) => {
    try {
      const { phone, otp } = req.body;
      const userId = (req.user as any).claims.sub;

      const user = await storage.getUser(userId);
      if (!user || user.otpCode !== otp || !user.otpExpiry || new Date() > user.otpExpiry) {
        return res.status(400).json({ message: "Invalid or expired OTP" });
      }

      // Mark phone as verified
      await storage.verifyUserPhone(userId, phone);

      res.json({ message: "Phone verified successfully" });
    } catch (error) {
      console.error("Error verifying OTP:", error);
      res.status(500).json({ message: "Failed to verify OTP" });
    }
  });

  // Emergency Alert Routes
  app.post("/api/emergency/alert", isAuthenticated, async (req: any, res) => {
    try {
      const { rideId, alertType, location, message } = req.body;
      const userId = (req.user as any).claims.sub;

      const alert = await storage.createEmergencyAlert({
        userId,
        rideId,
        alertType,
        location,
        message,
      });

      // In production, notify emergency services and contacts
      console.log(`Emergency alert from user ${userId}:`, alert);

      res.json(alert);
    } catch (error) {
      console.error("Error creating emergency alert:", error);
      res.status(500).json({ message: "Failed to create emergency alert" });
    }
  });

  // Chat Routes
  app.get("/api/chat/:rideId", isAuthenticated, async (req: any, res) => {
    try {
      const { rideId } = req.params;
      const messages = await storage.getRideMessages(rideId);
      res.json(messages);
    } catch (error) {
      console.error("Error fetching chat messages:", error);
      res.status(500).json({ message: "Failed to fetch messages" });
    }
  });

  app.post("/api/chat/:rideId/messages", isAuthenticated, async (req: any, res) => {
    try {
      const { rideId } = req.params;
      const { message, messageType = "text" } = req.body;
      const senderId = (req.user as any).claims.sub;

      const chatMessage = await storage.createChatMessage({
        rideId,
        senderId,
        message,
        messageType,
      });

      res.json(chatMessage);
    } catch (error) {
      console.error("Error sending message:", error);
      res.status(500).json({ message: "Failed to send message" });
    }
  });

  // ID Verification Routes
  app.post("/api/verify/upload", isAuthenticated, async (req: any, res) => {
    try {
      const { documentType, documentUrl } = req.body;
      const userId = (req.user as any).claims.sub;

      const verification = await storage.createIdVerification({
        userId,
        documentType,
        documentUrl,
        status: "pending"
      });

      res.json(verification);
    } catch (error) {
      console.error("Error uploading ID verification:", error);
      res.status(500).json({ message: "Failed to upload verification" });
    }
  });

  app.get("/api/verify/status", isAuthenticated, async (req: any, res) => {
    try {
      const userId = (req.user as any).claims.sub;
      const verifications = await storage.getUserIdVerifications(userId);
      res.json(verifications);
    } catch (error) {
      console.error("Error fetching verification status:", error);
      res.status(500).json({ message: "Failed to fetch verification status" });
    }
  });

  // Admin Routes
  app.get("/api/admin/users", isAuthenticated, async (req: any, res) => {
    try {
      // Note: In production, add admin role checking
      const users = await storage.getAllUsers();
      res.json(users);
    } catch (error) {
      console.error("Error fetching users:", error);
      res.status(500).json({ message: "Failed to fetch users" });
    }
  });

  app.put("/api/admin/users/:userId/suspend", isAuthenticated, async (req: any, res) => {
    try {
      const { userId } = req.params;
      const { suspended } = req.body;

      const user = await storage.suspendUser(userId, suspended);
      res.json(user);
    } catch (error) {
      console.error("Error updating user suspension:", error);
      res.status(500).json({ message: "Failed to update user" });
    }
  });

  app.get("/api/admin/verifications", isAuthenticated, async (req: any, res) => {
    try {
      const verifications = await storage.getPendingIdVerifications();
      res.json(verifications);
    } catch (error) {
      console.error("Error fetching pending verifications:", error);
      res.status(500).json({ message: "Failed to fetch verifications" });
    }
  });

  app.put("/api/admin/verifications/:id", isAuthenticated, async (req: any, res) => {
    try {
      const { id } = req.params;
      const { status, reviewNotes } = req.body;
      const reviewerId = (req.user as any).claims.sub;

      const verification = await storage.updateIdVerificationStatus(id, status, reviewNotes, reviewerId);
      res.json(verification);
    } catch (error) {
      console.error("Error updating verification:", error);
      res.status(500).json({ message: "Failed to update verification" });
    }
  });

  app.get("/api/admin/settings", isAuthenticated, async (req: any, res) => {
    try {
      const adminId = (req.user as any).claims.sub;
      const settings = await storage.getAdminSettings(adminId);
      res.json(settings);
    } catch (error) {
      console.error("Error fetching admin settings:", error);
      res.status(500).json({ message: "Failed to fetch settings" });
    }
  });

  app.post("/api/admin/settings", isAuthenticated, async (req: any, res) => {
    try {
      const adminId = (req.user as any).claims.sub;
      const settingsData = req.body;

      const settings = await storage.createAdminSettings({
        adminId,
        ...settingsData
      });

      res.json(settings);
    } catch (error) {
      console.error("Error creating admin settings:", error);
      res.status(500).json({ message: "Failed to create settings" });
    }
  });

  app.put("/api/admin/settings", isAuthenticated, async (req: any, res) => {
    try {
      const adminId = (req.user as any).claims.sub;
      const settingsData = req.body;

      const settings = await storage.updateAdminSettings(adminId, settingsData);
      res.json(settings);
    } catch (error) {
      console.error("Error updating admin settings:", error);
      res.status(500).json({ message: "Failed to update settings" });
    }
  });

  // PayPal Payment Routes
  app.get("/paypal/setup", async (req, res) => {
    try {
      // Mock implementation until PayPal keys are provided
      res.json({
        clientToken: "mock_client_token",
        message: "PayPal integration pending - need PAYPAL_CLIENT_ID and PAYPAL_CLIENT_SECRET"
      });
    } catch (error) {
      console.error("PayPal setup error:", error);
      res.status(500).json({ error: "PayPal setup failed" });
    }
  });

  app.post("/paypal/order", async (req, res) => {
    try {
      const { amount, currency = "USD", intent = "CAPTURE" } = req.body;

      // Mock implementation until PayPal keys are provided
      res.json({
        id: "mock_order_" + Date.now(),
        status: "CREATED",
        message: "PayPal integration pending - need PAYPAL_CLIENT_ID and PAYPAL_CLIENT_SECRET"
      });
    } catch (error) {
      console.error("PayPal order creation error:", error);
      res.status(500).json({ error: "Failed to create PayPal order" });
    }
  });

  app.post("/paypal/order/:orderID/capture", async (req, res) => {
    try {
      const { orderID } = req.params;

      // Mock implementation until PayPal keys are provided
      res.json({
        id: orderID,
        status: "COMPLETED",
        message: "PayPal integration pending - need PAYPAL_CLIENT_ID and PAYPAL_CLIENT_SECRET"
      });
    } catch (error) {
      console.error("PayPal capture error:", error);
      res.status(500).json({ error: "Failed to capture PayPal order" });
    }
  });

  app.get("/api/payment/transactions", isAuthenticated, async (req: any, res) => {
    try {
      const userId = (req.user as any).claims.sub;
      const transactions = await storage.getPaymentTransactions(userId);
      res.json(transactions);
    } catch (error) {
      console.error("Error fetching transactions:", error);
      res.status(500).json({ message: "Failed to fetch transactions" });
    }
  });

  // PayPal routes (demo mode - requires API credentials)
  app.get("/paypal/setup", async (req, res) => {
    res.json({ 
      clientToken: "demo_token_requires_paypal_credentials",
      message: "Demo mode - add PAYPAL_CLIENT_ID and PAYPAL_CLIENT_SECRET to process real payments"
    });
  });

  app.post("/paypal/order", async (req, res) => {
    res.json({ 
      id: "demo_order_id",
      message: "Demo mode - add PayPal credentials for real payment processing"
    });
  });

  app.post("/paypal/order/:orderID/capture", async (req, res) => {
    res.json({ 
      status: "COMPLETED",
      message: "Demo mode - payment would be processed with real PayPal credentials"
    });
  });

  const httpServer = createServer(app);

  // Initialize WebSocket server for real-time features
  const wsManager = new WebSocketManager(httpServer);

  // Add WebSocket manager to routes for notifications
  app.locals.wsManager = wsManager;

  return httpServer;
}

server/storage.ts.content:
import {
  users,
  vehicles,
  rides,
  rideRequests,
  reviews,
  chatMessages,
  emergencyAlerts,
  idVerifications,
  adminSettings,
  paymentTransactions,
  type User,
  type UpsertUser,
  type Vehicle,
  type InsertVehicle,
  type Ride,
  type InsertRide,
  type RideRequest,
  type InsertRideRequest,
  type Review,
  type InsertReview,
  type ChatMessage,
  type InsertChatMessage,
  type EmergencyAlert,
  type InsertEmergencyAlert,
  type IdVerification,
  type InsertIdVerification,
  type InsertAdminSettings,
  type AdminSettings,
  type RideWithDetails,
  type RideRequestWithDetails,
} from "@shared/schema";
import { db } from "./db";
import { eq, and, desc, asc, gte, lte, ne, sql, ilike } from "drizzle-orm";

export interface IStorage {
  // User operations - mandatory for Replit Auth
  getUser(id: string): Promise<User | undefined>;
  upsertUser(user: UpsertUser): Promise<User>;
  updateUserProfile(id: string, data: Partial<User>): Promise<User>;

  // Vehicle operations
  createVehicle(vehicle: InsertVehicle): Promise<Vehicle>;
  getUserVehicles(userId: string): Promise<Vehicle[]>;
  getVehicle(id: string): Promise<Vehicle | undefined>;
  updateVehicle(id: string, data: Partial<Vehicle>): Promise<Vehicle>;
  deleteVehicle(id: string): Promise<void>;

  // Ride operations
  createRide(ride: InsertRide): Promise<Ride>;
  getRide(id: string): Promise<RideWithDetails | undefined>;
  searchRides(filters: {
    origin?: string;
    destination?: string;
    date?: Date;
    minSeats?: number;
  }): Promise<RideWithDetails[]>;
  getUserRides(userId: string, type: 'driver' | 'passenger'): Promise<RideWithDetails[]>;
  updateRide(id: string, data: Partial<Ride>): Promise<Ride>;
  deleteRide(id: string): Promise<void>;

  // Ride request operations
  createRideRequest(request: InsertRideRequest): Promise<RideRequest>;
  getRideRequest(id: string): Promise<RideRequestWithDetails | undefined>;
  getRideRequests(rideId: string): Promise<RideRequestWithDetails[]>;
  getUserRideRequests(userId: string): Promise<RideRequestWithDetails[]>;
  updateRideRequestStatus(id: string, status: string): Promise<RideRequest>;

  // Review operations
  createReview(review: InsertReview): Promise<Review>;
  getRideReviews(rideId: string): Promise<Review[]>;
  getUserReviews(userId: string): Promise<Review[]>;

  // Advanced features
  updateUserOTP(id: string, otpCode: string, otpExpiry: Date): Promise<User>;

  // Payment operations
  processPayment(payment: {
    userId: string;
    rideId: string;
    amount: number;
    paymentMethod: string;
    status: string;
  }): Promise<any>;
  verifyUserPhone(id: string, phone: string): Promise<User>;

  // Chat operations
  createChatMessage(message: InsertChatMessage): Promise<ChatMessage>;
  getRideMessages(rideId: string): Promise<ChatMessage[]>;
  markMessagesAsRead(rideId: string, userId: string): Promise<void>;

  // Emergency alert operations
  createEmergencyAlert(alert: InsertEmergencyAlert): Promise<EmergencyAlert>;
  getEmergencyAlerts(userId?: string): Promise<EmergencyAlert[]>;
  updateEmergencyAlert(id: string, data: Partial<EmergencyAlert>): Promise<EmergencyAlert>;

  // ID verification operations
  createIdVerification(verification: InsertIdVerification): Promise<IdVerification>;
  getUserIdVerifications(userId: string): Promise<IdVerification[]>;
  getIdVerifications(userId: string): Promise<IdVerification[]>;
  updateIdVerificationStatus(id: string, status: string, reviewNotes?: string, reviewedBy?: string): Promise<IdVerification>;
  getPendingIdVerifications(): Promise<IdVerification[]>;

  // Admin operations
  createAdminSettings(settings: InsertAdminSettings): Promise<AdminSettings>;
  getAdminSettings(adminId: string): Promise<AdminSettings | undefined>;
  updateAdminSettings(adminId: string, data: Partial<AdminSettings>): Promise<AdminSettings>;
  getAllUsers(): Promise<User[]>;
  suspendUser(userId: string, suspended: boolean): Promise<User>;

  // Payment operations
  createPaymentTransaction(transaction: any): Promise<any>;
  updatePaymentTransaction(id: string, data: any): Promise<any>;
  getPaymentTransactions(userId?: string): Promise<any[]>;
}

export class DatabaseStorage implements IStorage {
  // User operations
  async getUser(id: string): Promise<User | undefined> {
    const [user] = await db.select().from(users).where(eq(users.id, id));
    return user;
  }

  async upsertUser(userData: UpsertUser): Promise<User> {
    const [user] = await db
      .insert(users)
      .values(userData)
      .onConflictDoUpdate({
        target: users.id,
        set: {
          ...userData,
          updatedAt: new Date(),
        },
      })
      .returning();
    return user;
  }

  async updateUserProfile(id: string, data: Partial<User>): Promise<User> {
    const [user] = await db
      .update(users)
      .set(data)
      .where(eq(users.id, id))
      .returning();
    return user;
  }

  // Vehicle operations
  async createVehicle(vehicle: InsertVehicle): Promise<Vehicle> {
    const [newVehicle] = await db.insert(vehicles).values(vehicle as any).returning();
    return newVehicle;
  }

  async getUserVehicles(userId: string): Promise<Vehicle[]> {
    return await db.select().from(vehicles).where(eq(vehicles.userId, userId));
  }

  async getVehicle(id: string): Promise<Vehicle | undefined> {
    const [vehicle] = await db.select().from(vehicles).where(eq(vehicles.id, id));
    return vehicle;
  }

  async updateVehicle(id: string, data: Partial<Vehicle>): Promise<Vehicle> {
    const [vehicle] = await db
      .update(vehicles)
      .set(data)
      .where(eq(vehicles.id, id))
      .returning();
    return vehicle;
  }

  async deleteVehicle(id: string): Promise<void> {
    await db.delete(vehicles).where(eq(vehicles.id, id));
  }

  // Ride operations
  async createRide(ride: InsertRide): Promise<Ride> {
    const [newRide] = await db.insert(rides).values(ride as any).returning();
    return newRide;
  }

  async getRide(id: string): Promise<RideWithDetails | undefined> {
    const result = await db
      .select()
      .from(rides)
      .leftJoin(users, eq(rides.driverId, users.id))
      .leftJoin(vehicles, eq(rides.vehicleId, vehicles.id))
      .where(eq(rides.id, id));

    if (result.length === 0) return undefined;

    const ride = result[0].rides;
    const driver = result[0].users!;
    const vehicle = result[0].vehicles!;

    // Get ride requests
    const requestsResult = await db
      .select()
      .from(rideRequests)
      .leftJoin(users, eq(rideRequests.passengerId, users.id))
      .where(eq(rideRequests.rideId, id));

    const requests = requestsResult.map(r => ({
      ...r.ride_requests,
      passenger: r.users!,
    }));

    return {
      ...ride,
      driver,
      vehicle,
      requests,
    };
  }

  async searchRides(filters: {
    origin?: string;
    destination?: string;
    date?: Date;
    minSeats?: number;
  }): Promise<RideWithDetails[]> {
    let query = db
      .select()
      .from(rides)
      .leftJoin(users, eq(rides.driverId, users.id))
      .leftJoin(vehicles, eq(rides.vehicleId, vehicles.id))
      .where(eq(rides.status, 'active'));

    // Apply filters
    const conditions = [eq(rides.status, 'active')];

    if (filters.origin) {
      conditions.push(eq(rides.origin, filters.origin));
    }

    if (filters.destination) {
      conditions.push(eq(rides.destination, filters.destination));
    }

    if (filters.date) {
      const startOfDay = new Date(filters.date);
      startOfDay.setHours(0, 0, 0, 0);
      const endOfDay = new Date(filters.date);
      endOfDay.setHours(23, 59, 59, 999);

      conditions.push(
        and(
          gte(rides.departureDate, startOfDay),
          lte(rides.departureDate, endOfDay)
        )!
      );
    }

    if (filters.minSeats) {
      conditions.push(gte(rides.availableSeats, filters.minSeats));
    }

    const finalQuery = db
      .select()
      .from(rides)
      .leftJoin(users, eq(rides.driverId, users.id))
      .leftJoin(vehicles, eq(rides.vehicleId, vehicles.id))
      .where(conditions.length > 0 ? and(...conditions) : undefined)
      .orderBy(asc(rides.departureDate));

    const result = await finalQuery;

    const ridesWithDetails: RideWithDetails[] = [];

    for (const row of result) {
      const ride = row.rides;
      const driver = row.users!;
      const vehicle = row.vehicles!;

      // Get ride requests for each ride
      const requestsResult = await db
        .select()
        .from(rideRequests)
        .leftJoin(users, eq(rideRequests.passengerId, users.id))
        .where(eq(rideRequests.rideId, ride.id));

      const requests = requestsResult.map(r => ({
        ...r.ride_requests,
        passenger: r.users!,
      }));

      ridesWithDetails.push({
        ...ride,
        driver,
        vehicle,
        requests,
      });
    }

    return ridesWithDetails;
  }

  async getUserRides(userId: string, type: 'driver' | 'passenger'): Promise<RideWithDetails[]> {
    if (type === 'driver') {
      const result = await db
        .select()
        .from(rides)
        .leftJoin(users, eq(rides.driverId, users.id))
        .leftJoin(vehicles, eq(rides.vehicleId, vehicles.id))
        .where(eq(rides.driverId, userId))
        .orderBy(desc(rides.createdAt));

      const ridesWithDetails: RideWithDetails[] = [];

      for (const row of result) {
        const ride = row.rides;
        const driver = row.users!;
        const vehicle = row.vehicles!;

        const requestsResult = await db
          .select()
          .from(rideRequests)
          .leftJoin(users, eq(rideRequests.passengerId, users.id))
          .where(eq(rideRequests.rideId, ride.id));

        const requests = requestsResult.map(r => ({
          ...r.ride_requests,
          passenger: r.users!,
        }));

        ridesWithDetails.push({
          ...ride,
          driver,
          vehicle,
          requests,
        });
      }

      return ridesWithDetails;
    } else {
      // Get rides where user has requests
      const result = await db
        .select()
        .from(rideRequests)
        .leftJoin(rides, eq(rideRequests.rideId, rides.id))
        .leftJoin(users, eq(rides.driverId, users.id))
        .leftJoin(vehicles, eq(rides.vehicleId, vehicles.id))
        .where(eq(rideRequests.passengerId, userId))
        .orderBy(desc(rideRequests.createdAt));

      const ridesWithDetails: RideWithDetails[] = [];

      for (const row of result) {
        if (!row.rides) continue;

        const ride = row.rides;
        const driver = row.users!;
        const vehicle = row.vehicles!;

        const requestsResult = await db
          .select()
          .from(rideRequests)
          .leftJoin(users, eq(rideRequests.passengerId, users.id))
          .where(eq(rideRequests.rideId, ride.id));

        const requests = requestsResult.map(r => ({
          ...r.ride_requests,
          passenger: r.users!,
        }));

        ridesWithDetails.push({
          ...ride,
          driver,
          vehicle,
          requests,
        });
      }

      return ridesWithDetails;
    }
  }

  async updateRide(id: string, data: Partial<Ride>): Promise<Ride> {
    const [ride] = await db
      .update(rides)
      .set(data)
      .where(eq(rides.id, id))
      .returning();
    return ride;
  }

  async deleteRide(id: string): Promise<void> {
    await db.delete(rides).where(eq(rides.id, id));
  }

  // Ride request operations
  async createRideRequest(request: InsertRideRequest): Promise<RideRequest> {
    const [newRequest] = await db.insert(rideRequests).values(request as any).returning();
    return newRequest;
  }

  async getRideRequest(id: string): Promise<RideRequestWithDetails | undefined> {
    const result = await db
      .select()
      .from(rideRequests)
      .leftJoin(rides, eq(rideRequests.rideId, rides.id))
      .leftJoin(users, eq(rideRequests.passengerId, users.id))
      .where(eq(rideRequests.id, id));

    if (result.length === 0) return undefined;

    const request = result[0].ride_requests;
    const ride = result[0].rides!;
    const passenger = result[0].users!;

    // Get full ride details
    const rideDetails = await this.getRide(ride.id);

    return {
      ...request,
      ride: rideDetails!,
      passenger,
    };
  }

  async getRideRequests(rideId: string): Promise<RideRequestWithDetails[]> {
    const result = await db
      .select()
      .from(rideRequests)
      .leftJoin(users, eq(rideRequests.passengerId, users.id))
      .where(eq(rideRequests.rideId, rideId))
      .orderBy(desc(rideRequests.createdAt));

    const requestsWithDetails: RideRequestWithDetails[] = [];

    for (const row of result) {
      const request = row.ride_requests;
      const passenger = row.users!;
      const rideDetails = await this.getRide(rideId);

      requestsWithDetails.push({
        ...request,
        ride: rideDetails!,
        passenger,
      });
    }

    return requestsWithDetails;
  }

  async getUserRideRequests(userId: string): Promise<RideRequestWithDetails[]> {
    const result = await db
      .select()
      .from(rideRequests)
      .leftJoin(users, eq(rideRequests.passengerId, users.id))
      .where(eq(rideRequests.passengerId, userId))
      .orderBy(desc(rideRequests.createdAt));

    const requestsWithDetails: RideRequestWithDetails[] = [];

    for (const row of result) {
      const request = row.ride_requests;
      const passenger = row.users!;
      const rideDetails = await this.getRide(request.rideId);

      requestsWithDetails.push({
        ...request,
        ride: rideDetails!,
        passenger,
      });
    }

    return requestsWithDetails;
  }

  async updateRideRequestStatus(id: string, status: string): Promise<RideRequest> {
    const [request] = await db
      .update(rideRequests)
      .set({ status } as any)
      .where(eq(rideRequests.id, id))
      .returning();
    return request;
  }

  // Review operations
  async createReview(review: InsertReview): Promise<Review> {
    const [newReview] = await db.insert(reviews).values(review as any).returning();
    return newReview;
  }

  async getRideReviews(rideId: string): Promise<Review[]> {
    return await db.select().from(reviews).where(eq(reviews.rideId, rideId));
  }

  async getUserReviews(userId: string): Promise<Review[]> {
    return await db.select().from(reviews).where(eq(reviews.revieweeId, userId))
      .orderBy(desc(reviews.createdAt));
  }

  // Advanced features implementation
  async updateUserOTP(id: string, otpCode: string, otpExpiry: Date): Promise<User> {
    const [user] = await db
      .update(users)
      .set({ otpCode, otpExpiry, updatedAt: new Date() })
      .where(eq(users.id, id))
      .returning();
    return user;
  }

  async verifyUserPhone(id: string, phone: string): Promise<User> {
    const [user] = await db
      .update(users)
      .set({ 
        phone, 
        phoneVerified: true, 
        otpCode: null, 
        otpExpiry: null,
        updatedAt: new Date() 
      })
      .where(eq(users.id, id))
      .returning();
    return user;
  }

  async createChatMessage(message: InsertChatMessage): Promise<ChatMessage> {
    const [newMessage] = await db.insert(chatMessages).values(message as any).returning();
    return newMessage;
  }

  async getChatMessages(rideId: string): Promise<ChatMessage[]> {
    const result = await db
      .select()
      .from(chatMessages)
      .leftJoin(users, eq(chatMessages.senderId, users.id))
      .where(eq(chatMessages.rideId, rideId))
      .orderBy(asc(chatMessages.createdAt));

    return result.map(row => ({
      ...row.chat_messages,
      sender: row.users!,
    })) as any;
  }

  async createEmergencyAlert(alert: InsertEmergencyAlert): Promise<EmergencyAlert> {
    const [newAlert] = await db.insert(emergencyAlerts).values(alert as any).returning();
    return newAlert;
  }

  async getEmergencyAlerts(userId: string): Promise<EmergencyAlert[]> {
    return await db.select().from(emergencyAlerts)
      .where(eq(emergencyAlerts.userId, userId))
      .orderBy(desc(emergencyAlerts.createdAt));
  }

  async createIdVerification(verification: InsertIdVerification): Promise<IdVerification> {
    const [newVerification] = await db.insert(idVerifications).values(verification as any).returning();
    return newVerification;
  }

  async getIdVerifications(userId: string): Promise<IdVerification[]> {
    return await db.select().from(idVerifications)
      .where(eq(idVerifications.userId, userId))
      .orderBy(desc(idVerifications.createdAt));
  }

  async getUserIdVerifications(userId: string): Promise<IdVerification[]> {
    return this.getIdVerifications(userId);
  }

  async getPendingIdVerifications(): Promise<IdVerification[]> {
    return await db.select().from(idVerifications)
      .where(eq(idVerifications.status, 'pending'))
      .orderBy(desc(idVerifications.createdAt));
  }

  async updateIdVerificationStatus(id: string, status: string, reviewNotes?: string, reviewedBy?: string): Promise<IdVerification> {
    const [verification] = await db
      .update(idVerifications)
      .set({ 
        reviewNotes,
        reviewedBy: reviewedBy || null,
        reviewedAt: new Date()
      } as any)
      .where(eq(idVerifications.id, id))
      .returning();
    return verification;
  }

  // Admin methods implementation
  async getAllUsers(): Promise<User[]> {
    return await db.select().from(users).orderBy(desc(users.createdAt));
  }

  async suspendUser(userId: string, suspended: boolean): Promise<User> {
    const [user] = await db
      .update(users)
      .set({ suspended })
      .where(eq(users.id, userId))
      .returning();
    return user;
  }

  async getAllRides(): Promise<RideWithDetails[]> {
    const result = await db
      .select()
      .from(rides)
      .leftJoin(users, eq(rides.driverId, users.id))
      .leftJoin(vehicles, eq(rides.vehicleId, vehicles.id))
      .orderBy(desc(rides.createdAt));

    return result.map(row => ({
      ...row.rides,
      driver: row.users!,
      vehicle: row.vehicles!,
      requests: [], // Simplified for admin view
    })) as RideWithDetails[];
  }

  async getAllEmergencyAlerts(): Promise<EmergencyAlert[]> {
    return await db.select().from(emergencyAlerts)
      .orderBy(desc(emergencyAlerts.createdAt));
  }

  async getAllIdVerifications(): Promise<IdVerification[]> {
    const result = await db
      .select()
      .from(idVerifications)
      .leftJoin(users, eq(idVerifications.userId, users.id))
      .orderBy(desc(idVerifications.createdAt));

    return result.map(row => ({
      ...row.id_verifications,
      user: row.users!,
    })) as any;
  }

  async getTotalUsers(): Promise<number> {
    const result = await db.select({ count: sql<number>`count(*)` }).from(users);
    return result[0].count;
  }

  async getNewUsersCount(days: number): Promise<number> {
    const dateThreshold = new Date();
    dateThreshold.setDate(dateThreshold.getDate() - days);

    const result = await db
      .select({ count: sql<number>`count(*)` })
      .from(users)
      .where(gte(users.createdAt, dateThreshold));

    return result[0].count;
  }

  async getActiveRidesCount(): Promise<number> {
    const result = await db
      .select({ count: sql<number>`count(*)` })
      .from(rides)
      .where(eq(rides.status, 'active'));

    return result[0].count;
  }

  async getCompletedRidesCount(days: number): Promise<number> {
    const dateThreshold = new Date();
    dateThreshold.setDate(dateThreshold.getDate() - days);

    const result = await db
      .select({ count: sql<number>`count(*)` })
      .from(rides)
      .where(and(
        eq(rides.status, 'completed'),
        gte(rides.createdAt, dateThreshold)
      ));

    return result[0].count;
  }

  async updateUserSuspension(id: string, suspended: boolean): Promise<User> {
    const [user] = await db
      .update(users)
      .set({ suspended, updatedAt: new Date() })
      .where(eq(users.id, id))
      .returning();
    return user;
  }

  // Admin settings implementation
  async getAdminSettings(adminId: string): Promise<AdminSettings | undefined> {
    const [settings] = await db.select().from(adminSettings)
      .where(eq(adminSettings.adminId, adminId));
    return settings;
  }

  async createAdminSettings(settings: InsertAdminSettings): Promise<AdminSettings> {
    const [newSettings] = await db.insert(adminSettings).values(settings as any).returning();
    return newSettings;
  }

  async updateAdminSettings(adminId: string, settingsData: Partial<InsertAdminSettings>): Promise<AdminSettings> {
    const [updatedSettings] = await db
      .update(adminSettings)
      .set({ ...settingsData } as any)
      .where(eq(adminSettings.adminId, adminId))
      .returning();
    return updatedSettings;
  }

  // Payment operations
  async getPaymentTransactions(userId?: string): Promise<any[]> {
    // Mock implementation for credit-based system
    return [];
  }

  async createPaymentTransaction(transaction: any): Promise<any> {
    // Mock implementation for credit-based system
    return { id: "tx_" + Date.now(), ...transaction };
  }

  async updatePaymentTransaction(id: string, data: any): Promise<any> {
    // Mock implementation for credit-based system
    return { id, ...data };
  }

  // Credit-based payment processing
  async processPayment(payment: {
    userId: string;
    rideId: string;
    amount: number;
    paymentMethod: string;
    status: string;
  }): Promise<any> {
    // In a real app, this would deduct credits from user's balance
    // and create a transaction record
    return {
      id: "payment_" + Date.now(),
      ...payment,
      createdAt: new Date().toISOString()
    };
  }

  // Missing chat operations
  async getRideMessages(rideId: string): Promise<ChatMessage[]> {
    return await db
      .select()
      .from(chatMessages)
      .where(eq(chatMessages.rideId, rideId))
      .orderBy(asc(chatMessages.createdAt));
  }

  async markMessagesAsRead(rideId: string, userId: string): Promise<void> {
    // Mock implementation - in real app would update read status
  }

  // Missing emergency operations
  async updateEmergencyAlert(id: string, data: Partial<EmergencyAlert>): Promise<EmergencyAlert> {
    const [alert] = await db
      .update(emergencyAlerts)
      .set(data)
      .where(eq(emergencyAlerts.id, id))
      .returning();
    return alert;
  }
}

export const storage = new DatabaseStorage();

