  --destructive: hsl(0, 62.8%, 30.6%);
  --destructive-foreground: hsl(0, 0%, 98%);
  --ring: hsl(195, 70%, 54%);
  --radius: 0.75rem;
}

@layer base {
  * {
    @apply border-border;
  }

  body {
    @apply font-sans antialiased bg-background text-foreground;
  }
}

@layer utilities {
  .text-sky-blue {
    color: hsl(194, 70%, 75%);
  }

  .text-sky-blue-dark {
    color: hsl(207, 78%, 59%);
  }

  .text-sky-blue-light {
    color: hsl(194, 100%, 94%);
  }

  .bg-sky-blue {
    background-color: hsl(194, 70%, 75%);
  }

  .bg-sky-blue-dark {
    background-color: hsl(207, 78%, 59%);
  }

  .bg-sky-blue-light {
    background-color: hsl(194, 100%, 94%);
  }

  .border-sky-blue {
    border-color: hsl(194, 70%, 75%);
  }

  .border-sky-blue-dark {
    border-color: hsl(207, 78%, 59%);
  }

  .border-sky-blue-light {
    border-color: hsl(194, 100%, 94%);
  }

  .hover\:bg-sky-blue:hover {
    background-color: hsl(194, 70%, 75%);
  }

  .hover\:bg-sky-blue-dark:hover {
    background-color: hsl(207, 78%, 59%);
  }

  .hover\:text-sky-blue:hover {
    color: hsl(194, 70%, 75%);
  }

  .hover\:text-sky-blue-dark:hover {
    color: hsl(207, 78%, 59%);
  }

  .focus\:ring-sky-blue:focus {
    --tw-ring-color: hsl(194, 70%, 75%);
  }

  .focus\:border-sky-blue:focus {
    border-color: hsl(194, 70%, 75%);
  }
}

/* Custom scrollbar for better UX */
.overflow-y-auto::-webkit-scrollbar {
  width: 6px;
}

.overflow-y-auto::-webkit-scrollbar-track {
  background: hsl(210, 40%, 96%);
  border-radius: 3px;
}

.overflow-y-auto::-webkit-scrollbar-thumb {
  background: hsl(195, 70%, 54%);
  border-radius: 3px;
}

.overflow-y-auto::-webkit-scrollbar-thumb:hover {
  background: hsl(207, 78%, 59%);
}

/* Mobile bottom navigation padding */
@media (max-width: 768px) {
  .pb-mobile-nav {
    padding-bottom: 5rem;
  }
}

/* Animation for loading states */
@keyframes pulse-sky {
  0%, 100% {
    opacity: 1;
  }
  50% {
    opacity: 0.5;
  }
}

.animate-pulse-sky {
  animation: pulse-sky 2s cubic-bezier(0.4, 0, 0.6, 1) infinite;
}

/* Gradient backgrounds for hero sections */
.bg-gradient-sky {
  background: linear-gradient(135deg, hsl(194, 100%, 94%) 0%, hsl(0, 0%, 100%) 100%);
}

/* Form focus states with sky blue theme */
.form-input:focus {
  --tw-ring-color: hsl(195, 70%, 54%);
  border-color: hsl(195, 70%, 54%);
}

/* Button variants for sky blue theme */
.btn-sky {
  background-color: hsl(195, 70%, 54%);
  color: hsl(210, 40%, 98%);
}

.btn-sky:hover {
  background-color: hsl(207, 78%, 59%);
}

.btn-sky-outline {
  border: 1px solid hsl(195, 70%, 54%);
  color: hsl(195, 70%, 54%);
  background-color: transparent;
}

.btn-sky-outline:hover {
  background-color: hsl(194, 100%, 94%);
}

/* Status badge colors */
.status-active {
  background-color: hsl(142, 76%, 90%);
  color: hsl(142, 76%, 36%);
}

.status-pending {
  background-color: hsl(48, 96%, 89%);
  color: hsl(48, 96%, 53%);
}

.status-completed {
  background-color: hsl(195, 100%, 90%);
  color: hsl(195, 100%, 45%);
}

.status-cancelled {
  background-color: hsl(0, 93%, 94%);
  color: hsl(0, 93%, 60%);
}

