import { useState } from "react";
import { Link, useLocation } from "wouter";
import { Button } from "@/components/ui/button";
import { Avatar, AvatarFallback, AvatarImage } from "@/components/ui/avatar";
import { Badge } from "@/components/ui/badge";
import { 
  DropdownMenu,
  DropdownMenuContent,
  DropdownMenuItem,
  DropdownMenuSeparator,
  DropdownMenuTrigger,
} from "@/components/ui/dropdown-menu";
import { useAuth } from "@/hooks/useAuth";
import type { User } from "@shared/schema";
import { Bell, User as UserIcon, History, Settings, LogOut, Home, Search, Car, MessageSquare, CheckCircle } from "lucide-react";
import { Popover, PopoverContent, PopoverTrigger } from "@/components/ui/popover";
import { ScrollArea } from "@/components/ui/scroll-area";
import { useIsMobile } from "@/hooks/use-mobile";

export default function Navigation() {
  const { user, isLoading } = useAuth() as { user: User | undefined; isLoading: boolean };
  const [location] = useLocation();
  const isMobile = useIsMobile();
  const [notifications] = useState(3); // Mock notification count
  const [notificationsList, setNotificationsList] = useState([
    {
      id: 1,
      type: "ride_request",
      title: "New ride request",
      message: "Sarah wants to join your ride to Downtown",
      time: "5 minutes ago",
      read: false,
      icon: Home,
      rideId: "ride-123",
      actionUrl: "/rides/ride-123/chat"
    },
    {
      id: 2,
      type: "message",
      title: "New message",
      message: "Driver: I'm running 5 minutes late",
      time: "15 minutes ago",
      read: false,
      icon: Bell,
      rideId: "ride-456",
      actionUrl: "/rides/ride-456/chat"
    },
    {
      id: 3,
      type: "ride_completed",
      title: "Ride completed",
      message: "Your ride to Airport has been completed",
      time: "2 hours ago",
      read: true,
      icon: History,
      rideId: "ride-789",
      actionUrl: "/history"
    }
  ]);

  const handleNotificationClick = (notification: any) => {
    // Mark notification as read
    setNotificationsList(prev => 
      prev.map(n => n.id === notification.id ? { ...n, read: true } : n)
    );

    // Navigate based on notification type using window.location
    if (notification.type === "ride_request") {
      // For ride requests, show a simple alert and go to home for now
      alert(`Ride request from Sarah! You can communicate with them through the ride details.`);
      window.location.href = "/";
    } else if (notification.type === "message") {
      // For messages, show alert and go to messages
      alert(`New message from your ride partner! Check your ride messages.`);
      window.location.href = "/messages";
    } else if (notification.type === "ride_completed") {
      // For completed rides, go to ride history
      window.location.href = "/history";
    } else {
      // Default: go to home
      window.location.href = "/";
    }
  };

  // Check if user is admin - temporarily allow current user for testing
  const isAdmin = user?.email === "admin@oneroute.com" || user?.id === "admin" || user?.email === "johanlabuschagne20@gmail.com";

  const navItems = [
    { path: "/", icon: Home, label: "Home" },
    { path: "/messages", icon: MessageSquare, label: "Messages" },
    { path: "/history", icon: History, label: "History" },
    { path: "/profile", icon: UserIcon, label: "Profile" },
    ...(isAdmin ? [{ path: "/admin", icon: Settings, label: "Admin" }] : []),
  ];

  return (
    <>
      {/* Desktop Navigation */}
      <nav className="bg-white shadow-sm border-b border-gray-200 sticky top-0 z-50">
        <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8">
          <div className="flex justify-between items-center h-16">
            {/* Logo */}
            <div className="flex items-center">
              <Link href="/">
                <div className="flex items-center space-x-3">
                  <img 
                    src="/one-route-logo.png" 
                    alt="One Route Logo" 
                    className="h-10 w-10 cursor-pointer hover:opacity-90 transition-opacity"
                  />
                  <span className="text-xl font-bold text-sky-600">One Route</span>
                </div>
              </Link>
            </div>

            {/* Desktop Navigation Items */}
            {!isMobile && (
              <div className="hidden md:flex items-center space-x-8">
                {navItems.map((item) => {
                  const Icon = item.icon;
                  const isActive = location === item.path;
                  return (
                    <Link key={item.path} href={item.path}>
                      <div className={`flex items-center space-x-2 px-3 py-2 rounded-md text-sm font-medium transition-colors cursor-pointer ${
                        isActive 
                          ? 'text-sky-600 bg-sky-50' 
                          : 'text-gray-600 hover:text-sky-600 hover:bg-sky-50'
                      }`}>
                        <Icon className="w-4 h-4" />
                        <span>{item.label}</span>
                      </div>
                    </Link>
                  );
                })}
              </div>
            )}

            {/* User Profile Section */}
            <div className="flex items-center space-x-4">
              {/* Notifications */}
              <Popover>
                <PopoverTrigger asChild>
                  <div className="relative">
                    <Button 
                      variant="ghost" 
                      size="sm" 
                      className="bg-sky-50 hover:bg-sky-100 text-sky-600 p-2 rounded-full"
                      aria-label="Notifications"
                    >
                      <Bell className="w-4 h-4" />
                    </Button>
                    {notifications > 0 && (
                      <Badge className="absolute -top-1 -right-1 bg-red-500 text-white text-xs rounded-full h-5 w-5 flex items-center justify-center p-0 min-w-0">
                        {notifications}
                      </Badge>
                    )}
                  </div>
                </PopoverTrigger>
                <PopoverContent className="w-80 p-0" align="end">
                  <div className="flex items-center justify-between p-4 border-b">
                    <h4 className="font-semibold">Notifications</h4>
                    <Button variant="ghost" size="sm" className="text-sky-600 hover:text-sky-700">
                      Mark all read
                    </Button>
                  </div>
                  <ScrollArea className="h-[400px]">
                    {notificationsList.length === 0 ? (
                      <div className="p-4 text-center text-gray-500">
                        <Bell className="w-8 h-8 mx-auto mb-2 text-gray-300" />
                        <p>No notifications yet</p>
                      </div>
                    ) : (
                      <div className="divide-y">
                        {notificationsList.map((notification) => {
                          const Icon = notification.icon;
                          return (
                            <div
                              key={notification.id}
                              className={`p-4 hover:bg-gray-50 cursor-pointer transition-colors ${
                                !notification.read ? 'bg-sky-50 border-l-4 border-l-sky-500' : ''
                              }`}
                              onClick={() => handleNotificationClick(notification)}
                            >
                              <div className="flex items-start space-x-3">
                                <div className={`p-2 rounded-full ${
                                  notification.type === 'ride_request' ? 'bg-blue-100 text-blue-600' :
                                  notification.type === 'message' ? 'bg-green-100 text-green-600' :
                                  'bg-gray-100 text-gray-600'
                                }`}>
                                  <Icon className="w-4 h-4" />
                                </div>
                                <div className="flex-1 min-w-0">
                                  <div className="flex items-start justify-between">
                                    <h5 className="text-sm font-medium text-gray-900 truncate">
                                      {notification.title}
                                    </h5>
                                    {!notification.read && (
                                      <div className="w-2 h-2 bg-sky-500 rounded-full ml-2" />
                                    )}
                                  </div>
                                  <p className="text-sm text-gray-600 mt-1">
                                    {notification.message}
                                  </p>
                                  <p className="text-xs text-gray-400 mt-1">
                                    {notification.time}
                                  </p>
                                </div>
                              </div>
                            </div>
                          );
                        })}
                      </div>
                    )}
                  </ScrollArea>
                  <div className="p-4 border-t bg-gray-50">
                    <Button variant="ghost" size="sm" className="w-full text-sky-600 hover:text-sky-700">
                      View all notifications
                    </Button>
                  </div>
                </PopoverContent>
              </Popover>

              {/* User Dropdown */}
              <DropdownMenu>
                <DropdownMenuTrigger asChild>
                  <div 
                    className="flex items-center space-x-2 cursor-pointer hover:bg-gray-50 rounded-lg p-2 transition-colors"
                    role="button"
                    aria-label="User menu"
                    tabIndex={0}
                  >
                    <Avatar className="w-8 h-8 border-2 border-sky-500">
                      <AvatarImage src={user?.profileImageUrl || ""} />
                      <AvatarFallback className="bg-sky-100 text-sky-700">
                        {user?.firstName?.[0]}{user?.lastName?.[0]}
                      </AvatarFallback>
                    </Avatar>
                    {!isMobile && (
                      <div className="hidden sm:block">
                        <p className="text-sm font-medium text-gray-700">
                          {user?.firstName} {user?.lastName}
                        </p>
                        <div className="flex items-center space-x-1">
                          <span className="text-xs text-yellow-500">â­</span>
                          <span className="text-xs text-gray-500">{user?.rating || "0.0"}</span>
                        </div>
                      </div>
                    )}
                  </div>
                </DropdownMenuTrigger>
                <DropdownMenuContent align="end" className="w-56">
                  <div className="px-2 py-1.5">
                    <p className="text-sm font-medium">{user?.firstName} {user?.lastName}</p>
                    <p className="text-xs text-gray-500">{user?.email}</p>
                  </div>
                  <DropdownMenuSeparator />

                  <Link href="/profile">
                    <DropdownMenuItem className="cursor-pointer">
                      <UserIcon className="w-4 h-4 mr-2" />
                      Profile
                    </DropdownMenuItem>
                  </Link>

                  <Link href="/history">
                    <DropdownMenuItem className="cursor-pointer">
                      <History className="w-4 h-4 mr-2" />
                      Ride History
                    </DropdownMenuItem>
                  </Link>

                  <Link href="/settings">
                    <DropdownMenuItem className="cursor-pointer">
                      <Settings className="w-4 h-4 mr-2" />
                      Settings
                    </DropdownMenuItem>
                  </Link>

                  <DropdownMenuSeparator />

                  <DropdownMenuItem 
                    className="cursor-pointer text-red-600 focus:text-red-600"
                    onClick={() => window.location.href = '/api/logout'}
                  >
                    <LogOut className="w-4 h-4 mr-2" />
                    Sign Out
                  </DropdownMenuItem>
                </DropdownMenuContent>
              </DropdownMenu>
            </div>
          </div>
        </div>
      </nav>

      {/* Mobile Bottom Navigation */}
      {isMobile && (
        <div className="fixed bottom-0 left-0 right-0 bg-white border-t border-gray-200 px-4 py-2 z-50">
          <div className="flex justify-around">
            {navItems.map((item) => {
              const Icon = item.icon;
              const isActive = location === item.path;
              return (
                <Link key={item.path} href={item.path}>
                  <div className={`flex flex-col items-center py-2 px-3 rounded-lg transition-colors cursor-pointer ${
                    isActive 
                      ? 'text-sky-600' 
                      : 'text-gray-400 hover:text-sky-600'
                  }`}>
                    <Icon className="w-5 h-5 mb-1" />
                    <span className="text-xs">{item.label}</span>
                  </div>
                </Link>
              );
            })}
          </div>
        </div>
      )}
    </>
  );
}

client/src/components/ride-card.tsx.content:
import { useState } from "react";
import { useMutation, useQueryClient } from "@tanstack/react-query";
import { Card, CardContent } from "@/components/ui/card";
import { Button } from "@/components/ui/button";
import { Badge } from "@/components/ui/badge";
import { Avatar, AvatarFallback, AvatarImage } from "@/components/ui/avatar";
import { useToast } from "@/hooks/use-toast";
import { apiRequest } from "@/lib/queryClient";
import UserProfileModal from "./user-profile-modal";
import { 
  MapPin, 
  Clock, 
  Users, 
  Car, 
  Star, 
  Shield,
  MessageCircle,
  CheckCircle,
  XCircle
} from "lucide-react";
import type { RideWithDetails } from "@shared/schema";
import { isUnauthorizedError } from "@/lib/authUtils";

interface RideCardProps {
  ride: RideWithDetails;
  mode: "driver" | "passenger";
}

// Default props validation
const defaultProps = {
  mode: "passenger" as const,
};

export default function RideCard({ ride, mode = defaultProps.mode }: RideCardProps) {
  // Validate required props
  if (!ride || !ride.id) {
    console.warn('RideCard: Invalid ride prop provided');
    return null;
  }
  const { toast } = useToast();
  const queryClient = useQueryClient();
  const [showProfileModal, setShowProfileModal] = useState(false);
  const [selectedUser, setSelectedUser] = useState<any>(null);

  const formatDate = (dateString: string) => {
    return new Date(dateString).toLocaleDateString("en-US", {
      weekday: "short",
      month: "short",
      day: "numeric",
      hour: "2-digit",
      minute: "2-digit",
    });
  };

  const getStatusBadge = (status: string) => {
    switch (status) {
      case "active":
        return <Badge className="bg-green-100 text-green-800">Active</Badge>;
      case "completed":
        return <Badge className="bg-blue-100 text-blue-800">Completed</Badge>;
      case "cancelled":
        return <Badge className="bg-red-100 text-red-800">Cancelled</Badge>;
      case "pending":
        return <Badge className="bg-yellow-100 text-yellow-800">Pending</Badge>;
      case "accepted":
        return <Badge className="bg-green-100 text-green-800">Accepted</Badge>;
      case "rejected":
        return <Badge className="bg-red-100 text-red-800">Rejected</Badge>;
      default:
        return <Badge variant="outline">{status}</Badge>;
    }
  };

  // Request ride mutation
  const requestRideMutation = useMutation({
    mutationFn: async () => {
      return await apiRequest("POST", "/api/ride-requests", {
        rideId: ride.id,
        seatsRequested: 1,
      });
    },
    onSuccess: () => {
      toast({
        title: "Success",
        description: "Ride request sent successfully!",
      });
      queryClient.invalidateQueries({ queryKey: ["/api/rides/search"] });
      queryClient.invalidateQueries({ queryKey: ["/api/rides"] });
    },
    onError: (error) => {
      if (isUnauthorizedError(error)) {
        toast({
          title: "Unauthorized",
          description: "You are logged out. Logging in again...",
          variant: "destructive",
        });
        setTimeout(() => {
          window.location.href = "/api/login";
        }, 500);
        return;
      }
      toast({
        title: "Error",
        description: error instanceof Error ? error.message : "Failed to send ride request. Please try again.",
        variant: "destructive",
      });
    },
  });

  // Accept/reject request mutations
  const updateRequestMutation = useMutation({
    mutationFn: async ({ requestId, status }: { requestId: string; status: string }) => {
      return await apiRequest("PUT", `/api/ride-requests/${requestId}/status`, { status });
    },
    onSuccess: () => {
      toast({
        title: "Success",
        description: "Request updated successfully!",
      });
      queryClient.invalidateQueries({ queryKey: ["/api/rides"] });
    },
    onError: (error) => {
      if (isUnauthorizedError(error)) {
        toast({
          title: "Unauthorized",
          description: "You are logged out. Logging in again...",
          variant: "destructive",
        });
        setTimeout(() => {
          window.location.href = "/api/login";
        }, 500);
        return;
      }
      const errorMessage = error instanceof Error ? error.message : "Failed to update request. Please try again.";
      toast({
        title: "Error",
        description: errorMessage.includes("Failed to update request") ? errorMessage : `Failed to update request: ${errorMessage}`,
        variant: "destructive",
      });
    },
  });

  const handleRequestRide = () => {
    requestRideMutation.mutate();
  };

  const handleAcceptRequest = (requestId: string) => {
    updateRequestMutation.mutate({ requestId, status: "accepted" });
  };

  const handleRejectRequest = (requestId: string) => {
    updateRequestMutation.mutate({ requestId, status: "rejected" });
  };

  const handleShowProfile = (user: any) => {
    setSelectedUser(user);
    setShowProfileModal(true);
  };

  const pendingRequests = ride.requests?.filter(req => req.status === "pending") || [];
  const acceptedRequests = ride.requests?.filter(req => req.status === "accepted") || [];

  return (
    <>
      <Card className="hover:shadow-md transition-shadow border-l-4 border-sky-500">
        <CardContent className="p-6">
          <div className="flex flex-col md:flex-row md:items-center justify-between">
            <div className="flex-1">
              {/* Driver Info */}
              <div className="flex items-center space-x-4 mb-3">
                <Avatar
                  className="w-12 h-12 cursor-pointer border-2 border-sky-500"
                  onClick={() => handleShowProfile(ride.driver)}
                >
                  <AvatarImage src={ride.driver.profileImageUrl || ""} />
                  <AvatarFallback>
                    {ride.driver.firstName?.[0]}{ride.driver.lastName?.[0]}
                  </AvatarFallback>
                </Avatar>
                <div>
                  <h4 
                    className="font-semibold text-gray-800 cursor-pointer hover:text-sky-600"
                    onClick={() => handleShowProfile(ride.driver)}
                  >
                    {ride.driver.firstName} {ride.driver.lastName}
                  </h4>
                  <div className="flex items-center space-x-2">
                    <div className="flex items-center space-x-1">
                      <Star className="w-4 h-4 text-yellow-500 fill-current" />
                      <span className="text-sm">{ride.driver.rating || "0.0"}</span>
                    </div>
                    <span className="text-gray-500 text-sm">({ride.driver.totalRides || 0} rides)</span>
                    {ride.driver.isVerified && (
                      <div className="flex items-center space-x-1 text-green-600">
                        <Shield className="w-3 h-3" />
                        <span className="text-xs">Verified</span>
                      </div>
                    )}
                  </div>
                </div>
              </div>

              {/* Route Info */}
              <div className="grid grid-cols-1 md:grid-cols-2 gap-4 mb-3">
                <div>
                  <div className="flex items-center text-gray-600 mb-1">
                    <MapPin className="w-4 h-4 text-sky-500 mr-2" />
                    <span className="text-sm">{ride.origin} â†’ {ride.destination}</span>
                  </div>
                  <div className="flex items-center text-gray-600">
                    <Clock className="w-4 h-4 text-sky-500 mr-2" />
                    <span className="text-sm">{formatDate(ride.departureDate.toString())}</span>
                  </div>
                </div>

                <div>
                  <div className="flex items-center text-gray-600 mb-1">
                    <Car className="w-4 h-4 text-sky-500 mr-2" />
                    <span className="text-sm">
                      {ride.vehicle.year} {ride.vehicle.make} {ride.vehicle.model} - {ride.vehicle.color}
                    </span>
                  </div>
                  <div className="flex items-center text-gray-600">
                    <Users className="w-4 h-4 text-sky-500 mr-2" />
                    <span className="text-sm">{ride.availableSeats} of {ride.vehicle.seats} seats available</span>
                  </div>
                </div>
              </div>

              {/* Status and Notes */}
              <div className="flex items-center space-x-2 mb-2">
                {getStatusBadge(ride.status)}
                {acceptedRequests.length > 0 && (
                  <Badge variant="outline">
                    {acceptedRequests.length} passenger{acceptedRequests.length > 1 ? 's' : ''} booked
                  </Badge>
                )}
              </div>

              {ride.notes && (
                <p className="text-sm text-gray-600 italic mb-3">"{ride.notes}"</p>
              )}
            </div>

            {/* Price and Actions */}
            <div className="flex flex-col items-end space-y-3 mt-4 md:mt-0">
              <div className="text-right">
                <div className="text-2xl font-bold text-sky-600">${ride.pricePerSeat}</div>
                <div className="text-sm text-gray-500">per person</div>
              </div>

              {mode === "passenger" && ride.status === "active" && (
                <Button 
                  onClick={handleRequestRide}
                  className="bg-sky-500 hover:bg-sky-600"
                  disabled={requestRideMutation.isPending}
                >
                  {requestRideMutation.isPending ? "Requesting..." : "Request Ride"}
                </Button>
              )}

              {mode === "driver" && (
                <div className="flex space-x-2">
                  <Button 
                    variant="outline"
                    size="sm"
                    className="flex items-center space-x-1"
                  >
                    <MessageCircle className="w-4 h-4" />
                    <span>Messages</span>
                  </Button>
                </div>
              )}
            </div>
          </div>

          {/* Pending Requests (Driver View) */}
          {mode === "driver" && pendingRequests.length > 0 && (
            <div className="mt-6 pt-4 border-t border-gray-200">
              <h5 className="font-medium text-gray-800 mb-3">Pending Requests</h5>
              <div className="space-y-3">
                {pendingRequests.map((request) => (
                  <div key={request.id} className="flex items-center justify-between p-3 bg-sky-50 rounded-lg">
                    <div className="flex items-center space-x-3">
                      <Avatar
                        className="w-8 h-8 cursor-pointer"
                        onClick={() => handleShowProfile(request.passenger)}
                      >
                        <AvatarImage src={request.passenger.profileImageUrl || ""} />
                        <AvatarFallback className="text-xs">
                          {request.passenger.firstName?.[0]}{request.passenger.lastName?.[0]}
                        </AvatarFallback>
                      </Avatar>
                      <div>
                        <p 
                          className="font-medium text-sm cursor-pointer hover:text-sky-600"
                          onClick={() => handleShowProfile(request.passenger)}
                        >
                          {request.passenger.firstName} {request.passenger.lastName}
                        </p>
                        <div className="flex items-center space-x-2">
                          <Star className="w-3 h-3 text-yellow-500 fill-current" />
                          <span className="text-xs text-gray-600">
                            {request.passenger.rating || "0.0"} ({request.passenger.totalRides || 0} rides)
                          </span>
                        </div>
                      </div>
                    </div>

                    <div className="flex space-x-2">
                      <Button
                        size="sm"
                        onClick={() => handleAcceptRequest(request.id)}
                        className="bg-green-500 hover:bg-green-600 text-white"
                        disabled={updateRequestMutation.isPending}
                      >
                        <CheckCircle className="w-3 h-3 mr-1" />
                        Accept
                      </Button>
                      <Button
                        size="sm"
                        variant="outline"
                        onClick={() => handleRejectRequest(request.id)}
                        className="text-red-600 border-red-200 hover:bg-red-50"
                        disabled={updateRequestMutation.isPending}
                      >
                        <XCircle className="w-3 h-3 mr-1" />
                        Decline
                      </Button>
                    </div>
                  </div>
                ))}
              </div>
            </div>
          )}

          {/* Accepted Passengers (Driver View) */}
          {mode === "driver" && acceptedRequests.length > 0 && (
            <div className="mt-6 pt-4 border-t border-gray-200">
              <h5 className="font-medium text-gray-800 mb-3">Confirmed Passengers</h5>
              <div className="space-y-2">
                {acceptedRequests.map((request) => (
                  <div key={request.id} className="flex items-center space-x-3 p-2">
                    <Avatar
                      className="w-6 h-6 cursor-pointer"
                      onClick={() => handleShowProfile(request.passenger)}
                    >
                      <AvatarImage src={request.passenger.profileImageUrl || ""} />
                      <AvatarFallback className="text-xs">
                        {request.passenger.firstName?.[0]}{request.passenger.lastName?.[0]}
                      </AvatarFallback>
                    </Avatar>
                    <span 
                      className="text-sm cursor-pointer hover:text-sky-600"
                      onClick={() => handleShowProfile(request.passenger)}
                    >
                      {request.passenger.firstName} {request.passenger.lastName}
                    </span>
                    <span className="text-xs text-gray-500">
                      ({request.seatsRequested} seat{request.seatsRequested > 1 ? 's' : ''})
                    </span>
                  </div>
                ))}
              </div>
            </div>
          )}
        </CardContent>
      </Card>

      {/* User Profile Modal */}
      {showProfileModal && selectedUser && (
        <UserProfileModal
          user={selectedUser}
          isOpen={showProfileModal}
          onClose={() => setShowProfileModal(false)}
        />
      )}
    </>
  );
}

client/src/components/ride-form.tsx.content:
import { useState } from "react";
import { useMutation, useQuery, useQueryClient } from "@tanstack/react-query";
import { useForm } from "react-hook-form";
import { zodResolver } from "@hookform/resolvers/zod";
import { Button } from "@/components/ui/button";
import { Input } from "@/components/ui/input";
import { Label } from "@/components/ui/label";
import { Textarea } from "@/components/ui/textarea";
import { Select, SelectContent, SelectItem, SelectTrigger, SelectValue } from "@/components/ui/select";
import { useToast } from "@/hooks/use-toast";
import { apiRequest } from "@/lib/queryClient";
import { insertRideSchema } from "@shared/schema";
import { z } from "zod";
import { MapPin, Calendar, Clock, Users, DollarSign, Car } from "lucide-react";
import { isUnauthorizedError } from "@/lib/authUtils";

const rideFormSchema = z.object({
  origin: z.string().min(1, "Origin is required"),
  destination: z.string().min(1, "Destination is required"),
  departureDate: z.string().min(1, "Departure date is required"),
  departureTime: z.string().min(1, "Departure time is required"),
  availableSeats: z.number().min(1, "At least 1 seat required"),
  pricePerSeat: z.string().min(1, "Price is required"),
  vehicleId: z.string().min(1, "Vehicle is required"),
  notes: z.string().optional(),
});

type RideFormData = z.infer<typeof rideFormSchema>;

export default function RideForm() {
  const { toast } = useToast();
  const queryClient = useQueryClient();

  const form = useForm<RideFormData>({
    resolver: zodResolver(rideFormSchema),
    defaultValues: {
      origin: "",
      destination: "",
      departureDate: "",
      departureTime: "",
      availableSeats: 1,
      pricePerSeat: "",
      vehicleId: "",
      notes: "",
    },
  });

  // Fetch user vehicles
  const { data: vehicles = [], isLoading: isLoadingVehicles } = useQuery<any[]>({
    queryKey: ["/api/vehicles"],
  });

  // Create ride mutation
  const createRideMutation = useMutation({
    mutationFn: async (data: RideFormData) => {
      // Combine date and time into a single datetime
      const departureDateTime = new Date(`${data.departureDate}T${data.departureTime}`);

      const rideData = {
        ...data,
        departureDate: departureDateTime.toISOString(),
        pricePerSeat: parseFloat(data.pricePerSeat),
      };

      // Remove the separate time field
      const { departureTime, ...finalData } = rideData;

      return await apiRequest("POST", "/api/rides", finalData);
    },
    onSuccess: () => {
      toast({
        title: "Success",
        description: "Ride created successfully!",
      });
      form.reset();
      queryClient.invalidateQueries({ queryKey: ["/api/rides"] });
    },
    onError: (error) => {
      if (isUnauthorizedError(error)) {
        toast({
          title: "Unauthorized",
          description: "You are logged out. Logging in again...",
          variant: "destructive",
        });
        setTimeout(() => {
          window.location.href = "/api/login";
        }, 500);
        return;
      }
      const errorMessage = error instanceof Error ? error.message : "Failed to create ride. Please try again.";
      toast({
        title: "Error",
        description: errorMessage.includes("Failed to create ride") ? errorMessage : `Failed to create ride: ${errorMessage}`,
        variant: "destructive",
      });
    },
  });

  const onSubmit = (data: RideFormData) => {
    // Additional validation before submission
    if (!data.origin?.trim() || !data.destination?.trim()) {
      toast({
        title: "Validation Error",
        description: "Please provide both pickup and destination locations.",
        variant: "destructive",
      });
      return;
    }

    if (!data.departureDate || !data.departureTime) {
      toast({
        title: "Validation Error", 
        description: "Please select both departure date and time.",
        variant: "destructive",
      });
      return;
    }

    // Check if departure time is in the future
    const departureDateTime = new Date(`${data.departureDate}T${data.departureTime}`);
    if (departureDateTime <= new Date()) {
      toast({
        title: "Validation Error",
        description: "Departure time must be in the future.",
        variant: "destructive",
      });
      return;
    }

    createRideMutation.mutate(data);
  };

  // Get today's date for min date validation (local timezone aware)
  const today = new Date(new Date().getTime() - new Date().getTimezoneOffset() * 60000).toISOString().split('T')[0];

  return (
    <form onSubmit={form.handleSubmit(onSubmit)} className="space-y-6">
      {/* Route Information */}
      <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
        <div>
          <Label htmlFor="origin" className="flex items-center space-x-2">
            <MapPin className="w-4 h-4 text-sky-500" />
            <span>From</span>
          </Label>
          <Input
            id="origin"
            placeholder="Departure location"
            {...form.register("origin")}
            className="focus:ring-sky-500 focus:border-sky-500"
          />
          {form.formState.errors.origin && (
            <p className="text-sm text-red-600 mt-1">{form.formState.errors.origin.message}</p>
          )}
        </div>

        <div>
          <Label htmlFor="destination" className="flex items-center space-x-2">
            <MapPin className="w-4 h-4 text-red-500" />
            <span>To</span>
          </Label>
          <Input
            id="destination"
            placeholder="Destination"
            {...form.register("destination")}
            className="focus:ring-sky-500 focus:border-sky-500"
          />
          {form.formState.errors.destination && (
            <p className="text-sm text-red-600 mt-1">{form.formState.errors.destination.message}</p>
          )}
        </div>
      </div>

      {/* Date and Time */}
      <div className="grid grid-cols-1 md:grid-cols-3 gap-4">
        <div>
          <Label htmlFor="departureDate" className="flex items-center space-x-2">
            <Calendar className="w-4 h-4 text-sky-500" />
            <span>Date</span>
          </Label>
          <Input
            id="departureDate"
            type="date"
            min={today}
            {...form.register("departureDate")}
            className="focus:ring-sky-500 focus:border-sky-500"
          />
          {form.formState.errors.departureDate && (
            <p className="text-sm text-red-600 mt-1">{form.formState.errors.departureDate.message}</p>
          )}
        </div>

        <div>
          <Label htmlFor="departureTime" className="flex items-center space-x-2">
            <Clock className="w-4 h-4 text-sky-500" />
            <span>Departure Time</span>
          </Label>
          <Input
            id="departureTime"
            type="time"
            {...form.register("departureTime")}
            className="focus:ring-sky-500 focus:border-sky-500"
          />
          {form.formState.errors.departureTime && (
            <p className="text-sm text-red-600 mt-1">{form.formState.errors.departureTime.message}</p>
          )}
        </div>

        <div>
          <Label htmlFor="availableSeats" className="flex items-center space-x-2">
            <Users className="w-4 h-4 text-sky-500" />
            <span>Available Seats</span>
          </Label>
          <Select 
            value={form.watch("availableSeats")?.toString()} 
            onValueChange={(value) => form.setValue("availableSeats", parseInt(value))}
          >
            <SelectTrigger className="focus:ring-sky-500 focus:border-sky-500">
              <SelectValue placeholder="Select seats" />
            </SelectTrigger>
            <SelectContent>
              <SelectItem value="1">1 seat</SelectItem>
              <SelectItem value="2">2 seats</SelectItem>
              <SelectItem value="3">3 seats</SelectItem>
              <SelectItem value="4">4 seats</SelectItem>
              <SelectItem value="5">5 seats</SelectItem>
              <SelectItem value="6">6 seats</SelectItem>
              <SelectItem value="7">7 seats</SelectItem>
            </SelectContent>
          </Select>
          {form.formState.errors.availableSeats && (
            <p className="text-sm text-red-600 mt-1">{form.formState.errors.availableSeats.message}</p>
          )}
        </div>
      </div>

      {/* Price and Vehicle */}
      <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
        <div>
          <Label htmlFor="pricePerSeat" className="flex items-center space-x-2">
            <DollarSign className="w-4 h-4 text-sky-500" />
            <span>Price per Seat</span>
          </Label>
          <Input
            id="pricePerSeat"
            type="number"
            step="0.01"
            min="0"
            placeholder="0.00"
            {...form.register("pricePerSeat")}
            className="focus:ring-sky-500 focus:border-sky-500"
          />
          {form.formState.errors.pricePerSeat && (
            <p className="text-sm text-red-600 mt-1">{form.formState.errors.pricePerSeat.message}</p>
          )}
        </div>

        <div>
          <Label htmlFor="vehicleId" className="flex items-center space-x-2">
            <Car className="w-4 h-4 text-sky-500" />
            <span>Vehicle</span>
          </Label>
          {isLoadingVehicles ? (
            <div className="flex items-center justify-center py-2">
              <div className="animate-spin w-4 h-4 border-2 border-sky-500 border-t-transparent rounded-full" />
            </div>
          ) : (vehicles as any[]).length === 0 ? (
            <div className="p-3 border rounded-lg text-center text-gray-500">
              <p className="text-sm">No vehicles found.</p>
              <p className="text-xs">Add a vehicle in your profile first.</p>
            </div>
          ) : (
            <Select 
              value={form.watch("vehicleId")} 
              onValueChange={(value) => form.setValue("vehicleId", value)}
            >
              <SelectTrigger className="focus:ring-sky-500 focus:border-sky-500">
                <SelectValue placeholder="Select your vehicle" />
              </SelectTrigger>
              <SelectContent>
                {(vehicles as any[]).map((vehicle: any) => (
                  <SelectItem key={vehicle.id} value={vehicle.id}>
                    {vehicle.year} {vehicle.make} {vehicle.model} - {vehicle.color} ({vehicle.licensePlate})
                  </SelectItem>
                ))}
              </SelectContent>
            </Select>
          )}
          {form.formState.errors.vehicleId && (
            <p className="text-sm text-red-600 mt-1">{form.formState.errors.vehicleId.message}</p>
          )}
        </div>
      </div>

      {/* Additional Notes */}
      <div>
        <Label htmlFor="notes">Additional Notes (Optional)</Label>
        <Textarea
          id="notes"
          rows={3}
          placeholder="Any special instructions, preferences, or additional information..."
          {...form.register("notes")}
          className="focus:ring-sky-500 focus:border-sky-500"
        />
      </div>

      {/* Submit Button */}
      <Button 
        type="submit" 
        className="w-full bg-sky-500 hover:bg-sky-600 text-white font-semibold py-3"
        disabled={createRideMutation.isPending || (vehicles as any[]).length === 0}
      >
        {createRideMutation.isPending ? (
          <div className="flex items-center space-x-2">
            <div className="animate-spin w-4 h-4 border-2 border-white border-t-transparent rounded-full" />
            <span>Creating Ride...</span>
          </div>
        ) : (
          <div className="flex items-center space-x-2">
            <span>Create Ride</span>
          </div>
        )}
      </Button>

      {(vehicles as any[]).length === 0 && (
        <p className="text-sm text-gray-600 text-center">
          You need to add a vehicle in your profile before creating rides.
        </p>
      )}
    </form>
  );
}

client/src/components/user-profile-modal.content:
import { useQuery } from "@tanstack/react-query";
import { Dialog, DialogContent, DialogHeader, DialogTitle } from "@/components/ui/dialog";
import { Button } from "@/components/ui/button";
import { Avatar, AvatarFallback, AvatarImage } from "@/components/ui/avatar";
import { Badge } from "@/components/ui/badge";
import { Card, CardContent } from "@/components/ui/card";
import { Star, Shield, Phone, MessageCircle, Share, Car } from "lucide-react";
import { useToast } from "@/hooks/use-toast";
import type { User } from "@shared/schema";

interface UserProfileModalProps {
  user: User;
  isOpen: boolean;
  onClose: () => void;
}

export default function UserProfileModal({ user, isOpen, onClose }: UserProfileModalProps) {
  // Validate required props
  if (!user || !user.id) {
    console.warn('UserProfileModal: Invalid user prop provided');
    return null;
  }
  const { toast } = useToast();

  // Fetch user reviews
  const { data: reviews = [], isLoading: isLoadingReviews } = useQuery<any[]>({
    queryKey: ["/api/reviews", user.id],
    enabled: isOpen && !!user.id,
  });

  const handleMessage = () => {
    toast({
      title: "Coming Soon",
      description: "In-app messaging will be available soon!",
    });
  };

  const handleShare = () => {
    if (navigator.share) {
      navigator.share({
        title: `${user.firstName} ${user.lastName} - One Route Profile`,
        text: `Check out ${user.firstName}'s profile on One Route`,
        url: window.location.href,
      });
    } else {
      navigator.clipboard.writeText(window.location.href);
      toast({
        title: "Link Copied",
        description: "Profile link copied to clipboard!",
      });
    }
  };

  const averageRating = user.rating ? parseFloat(user.rating) : 0;
  const totalRides = user.totalRides || 0;

  return (
    <Dialog open={isOpen} onOpenChange={onClose}>
      <DialogContent className="max-w-lg max-h-[90vh] overflow-y-auto">
        <DialogHeader>
          <DialogTitle>Profile Details</DialogTitle>
        </DialogHeader>

        <div className="space-y-6">
          {/* Profile Header */}
          <div className="flex items-center space-x-4">
            <Avatar className="w-20 h-20 border-4 border-sky-500">
              <AvatarImage src={user.profileImageUrl || ""} />
              <AvatarFallback className="text-xl">
                {user.firstName?.[0]}{user.lastName?.[0]}
              </AvatarFallback>
            </Avatar>
            <div className="flex-1">
              <h3 className="text-xl font-semibold text-gray-800">
                {user.firstName} {user.lastName}
              </h3>
              <div className="flex items-center space-x-2 mt-1">
                <div className="flex items-center space-x-1">
                  <Star className="w-4 h-4 text-yellow-500 fill-current" />
                  <span className="font-medium">{averageRating.toFixed(1)}</span>
                </div>
                <span className="text-gray-600">({totalRides} rides)</span>
              </div>
              <div className="flex space-x-2 mt-2">
                {user.isVerified && (
                  <Badge className="bg-green-100 text-green-800">
                    <Shield className="w-3 h-3 mr-1" />
                    ID Verified
                  </Badge>
                )}
                {user.phone && (
                  <Badge className="bg-blue-100 text-blue-800">
                    <Phone className="w-3 h-3 mr-1" />
                    Phone Verified
                  </Badge>
                )}
              </div>
            </div>
          </div>

          {/* Contact Information */}
          {(user.age || user.gender || user.address) && (
            <Card>
              <CardContent className="pt-4">
                <h4 className="font-medium text-gray-700 mb-3">About</h4>
                <div className="space-y-2 text-sm">
                  {user.age && (
                    <p><span className="text-gray-600">Age:</span> {user.age}</p>
                  )}
                  {user.gender && (
                    <p><span className="text-gray-600">Gender:</span> {user.gender}</p>
                  )}
                  {user.address && (
                    <p><span className="text-gray-600">Location:</span> {user.address}</p>
                  )}
                </div>
              </CardContent>
            </Card>
          )}

          {/* Recent Reviews */}
          <Card>
            <CardContent className="pt-4">
              <h4 className="font-medium text-gray-700 mb-3">Recent Reviews</h4>
              {isLoadingReviews ? (
                <div className="flex justify-center py-4">
                  <div className="animate-spin w-5 h-5 border-2 border-sky-500 border-t-transparent rounded-full" />
                </div>
              ) : reviews.length === 0 ? (
                <p className="text-gray-500 text-sm text-center py-4">
                  No reviews yet
                </p>
              ) : (
                <div className="space-y-3 max-h-32 overflow-y-auto">
                  {reviews.slice(0, 3).map((review: any) => (
                    <div key={review.id} className="p-3 bg-gray-50 rounded-lg">
                      <div className="flex items-center justify-between mb-1">
                        <div className="flex items-center space-x-1">
                          {[...Array(5)].map((_, i) => (
                            <Star
                              key={i}
                              className={`w-3 h-3 ${
                                i < review.rating 
                                  ? 'text-yellow-500 fill-current' 
                                  : 'text-gray-300'
                              }`}
                            />
                          ))}
                        </div>
                        <span className="text-xs text-gray-500">
                          {new Date(review.createdAt).toLocaleDateString()}
                        </span>
                      </div>
                      {review.comment && (
                        <p className="text-xs text-gray-600 line-clamp-2">
                          {review.comment}
                        </p>
                      )}
                    </div>
                  ))}
                  {reviews.length > 3 && (
                    <p className="text-xs text-sky-600 text-center">
                      +{reviews.length - 3} more reviews
                    </p>
                  )}
                </div>
              )}
            </CardContent>
          </Card>

          {/* Rating Breakdown */}
          {reviews.length > 0 && (
            <Card>
              <CardContent className="pt-4">
                <h4 className="font-medium text-gray-700 mb-3">Rating Breakdown</h4>
                <div className="space-y-2">
                  {[5, 4, 3, 2, 1].map((rating) => {
                    const count = reviews.filter((r: any) => r.rating === rating).length;
                    const percentage = reviews.length > 0 ? (count / reviews.length) * 100 : 0;

                    return (
                      <div key={rating} className="flex items-center space-x-2">
                        <div className="flex items-center space-x-1 w-12">
                          <span className="text-sm">{rating}</span>
                          <Star className="w-3 h-3 text-yellow-500 fill-current" />
                        </div>
                        <div className="flex-1 bg-gray-200 rounded-full h-2">
                          <div
                            className="bg-yellow-500 h-2 rounded-full"
                            style={{ width: `${percentage}%` }}
                          />
                        </div>
                        <span className="text-xs text-gray-600 w-8">{count}</span>
                      </div>
                    );
                  })}
                </div>
              </CardContent>
            </Card>
          )}

          {/* Action Buttons */}
          <div className="flex space-x-3">
            <Button 
              onClick={handleMessage}
              className="flex-1 bg-sky-500 hover:bg-sky-600"
            >
              <MessageCircle className="w-4 h-4 mr-2" />
              Message
            </Button>
            <Button 
              onClick={handleShare}
              variant="outline"
              className="flex-1"
            >
              <Share className="w-4 h-4 mr-2" />
              Share Profile
            </Button>
          </div>
        </div>
      </DialogContent>
    </Dialog>
  );
}

client/src/hooks/use-mobile.tsx.content:
import * as React from "react"

const MOBILE_BREAKPOINT = 768

export function useIsMobile() {
  const [isMobile, setIsMobile] = React.useState<boolean | undefined>(undefined)

  React.useEffect(() => {
    const mql = window.matchMedia(`(max-width: ${MOBILE_BREAKPOINT - 1}px)`)
    const onChange = () => {
      setIsMobile(window.innerWidth < MOBILE_BREAKPOINT)
    }
    mql.addEventListener("change", onChange)
    setIsMobile(window.innerWidth < MOBILE_BREAKPOINT)
    return () => mql.removeEventListener("change", onChange)
  }, [])

  return !!isMobile
}

client/src/hooks/use-toast.ts.content:
import * as React from "react"

import type {
  ToastActionElement,
  ToastProps,
} from "@/components/ui/toast"

const TOAST_LIMIT = 1
const TOAST_REMOVE_DELAY = 1000000

type ToasterToast = ToastProps & {
  id: string
  title?: React.ReactNode
  description?: React.ReactNode
  action?: ToastActionElement
}

const actionTypes = {
  ADD_TOAST: "ADD_TOAST",
  UPDATE_TOAST: "UPDATE_TOAST",
  DISMISS_TOAST: "DISMISS_TOAST",
  REMOVE_TOAST: "REMOVE_TOAST",
} as const

let count = 0

function genId() {
  count = (count + 1) % Number.MAX_SAFE_INTEGER
  return count.toString()
}

type ActionType = typeof actionTypes

type Action =
  | {
      type: ActionType["ADD_TOAST"]
      toast: ToasterToast
    }
  | {
      type: ActionType["UPDATE_TOAST"]
      toast: Partial<ToasterToast>
    }
  | {
      type: ActionType["DISMISS_TOAST"]
      toastId?: ToasterToast["id"]
    }
  | {
      type: ActionType["REMOVE_TOAST"]
      toastId?: ToasterToast["id"]
    }

interface State {
  toasts: ToasterToast[]
}

const toastTimeouts = new Map<string, ReturnType<typeof setTimeout>>()

const addToRemoveQueue = (toastId: string) => {
  if (toastTimeouts.has(toastId)) {
    return
  }

  const timeout = setTimeout(() => {
    toastTimeouts.delete(toastId)
    dispatch({
      type: "REMOVE_TOAST",
      toastId: toastId,
    })
  }, TOAST_REMOVE_DELAY)

  toastTimeouts.set(toastId, timeout)
}

export const reducer = (state: State, action: Action): State => {
  switch (action.type) {
    case "ADD_TOAST":
      return {
        ...state,
        toasts: [action.toast, ...state.toasts].slice(0, TOAST_LIMIT),
      }

    case "UPDATE_TOAST":
      return {
        ...state,
        toasts: state.toasts.map((t) =>
          t.id === action.toast.id ? { ...t, ...action.toast } : t
        ),
      }

    case "DISMISS_TOAST": {
      const { toastId } = action

      // ! Side effects ! - This could be extracted into a dismissToast() action,
      // but I'll keep it here for simplicity
      if (toastId) {
        addToRemoveQueue(toastId)
      } else {
        state.toasts.forEach((toast) => {
          addToRemoveQueue(toast.id)
        })
      }

      return {
        ...state,
        toasts: state.toasts.map((t) =>
          t.id === toastId || toastId === undefined
            ? {
                ...t,
                open: false,
              }
            : t
        ),
      }
    }
    case "REMOVE_TOAST":
      if (action.toastId === undefined) {
        return {
          ...state,
          toasts: [],
        }
      }
      return {
        ...state,
        toasts: state.toasts.filter((t) => t.id !== action.toastId),
      }
  }
}

const listeners: Array<(state: State) => void> = []

let memoryState: State = { toasts: [] }

function dispatch(action: Action) {
  memoryState = reducer(memoryState, action)
  listeners.forEach((listener) => {
    listener(memoryState)
  })
}

type Toast = Omit<ToasterToast, "id">

function toast({ ...props }: Toast) {
  const id = genId()

  const update = (props: ToasterToast) =>
    dispatch({
      type: "UPDATE_TOAST",
      toast: { ...props, id },
    })
  const dismiss = () => dispatch({ type: "DISMISS_TOAST", toastId: id })

  dispatch({
    type: "ADD_TOAST",
    toast: {
      ...props,
      id,
      open: true,
      onOpenChange: (open) => {
        if (!open) dismiss()
      },
    },
  })

  return {
    id: id,
    dismiss,
    update,
  }
}

function useToast() {
  const [state, setState] = React.useState<State>(memoryState)

  React.useEffect(() => {
    listeners.push(setState)
    return () => {
      const index = listeners.indexOf(setState)
      if (index > -1) {
        listeners.splice(index, 1)
      }
    }
  }, [state])

  return {
    ...state,
    toast,
    dismiss: (toastId?: string) => dispatch({ type: "DISMISS_TOAST", toastId }),
  }
}

export { useToast, toast }

